import styled from "styled-components";
import baseTheme from "../../../../style/themes/base";

// Styles copied from https://github.com/gpbl/react-day-picker/blob/v6.1.1/src/style.css
const addReactDayPickerStyles = () => `
  .DayPicker {
    display: inline-block;
  }

  .DayPicker-wrapper {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    position: relative;
    user-select: none;
    flex-direction: row;
    padding: 1rem 0;
  }

  .DayPicker-Month {
    display: table;
    border-collapse: collapse;
    border-spacing: 0;
    user-select: none;
    margin: 0 1rem;
  }

  .DayPicker-NavBar {
    position: absolute;
    left: 0;
    right: 0;
  }

  .DayPicker-NavButton {
    position: absolute;
    width: 1.5rem;
    height: 1.5rem;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    cursor: pointer;
  }

  .DayPicker-NavButton--prev {
    left: 1rem;
    background-image: url("data:image/svg+xml;base64,");
  }

  .DayPicker-NavButton--next {
    right: 1rem;
    background-image: url("data:image/svg+xml;base64,");
  }

  .DayPicker-NavButton--interactionDisabled {
    display: none;
  }

  .DayPicker-Caption {
    display: table-caption;
    height: 1.5rem;
    text-align: center;
  }

  .DayPicker-Weekdays {
    display: table-header-group;
  }

  .DayPicker-WeekdaysRow {
    display: table-row;
  }

  .DayPicker-Weekday {
    display: table-cell;

    abbr {
      text-decoration: none;
    }
  }

  .DayPicker-Body {
    display: table-row-group;
  }

  .DayPicker-Week {
    display: table-row;
  }

  .DayPicker-Day {
    display: table-cell;
    padding: 0.5rem;
    border: 1px solid #eaecec;
    text-align: center;
    cursor: pointer;
    vertical-align: middle;
  }

  .DayPicker-WeekNumber {
    display: table-cell;
    padding: 0.5rem;
    text-align: right;
    vertical-align: middle;
    min-width: 1rem;
    font-size: 0.75em;
    cursor: pointer;
    color: #8b9898;
  }

  .DayPicker--interactionDisabled .DayPicker-Day {
    cursor: default;
  }

  .DayPicker-Footer {
    display: table-caption;
    caption-side: bottom;
    padding-top: 0.5rem;
  }

  .DayPicker-TodayButton {
    border: none;
    background-image: none;
    background-color: transparent;
    box-shadow: none;
    cursor: pointer;
    color: #4a90e2;
    font-size: 0.875em;
  }

  /* Default modifiers */

  .DayPicker-Day--today {
    color: #d0021b;
    font-weight: 500;
  }

  .DayPicker-Day--disabled {
    color: #dce0e0;
    cursor: default;
    background-color: #eff1f1;
  }

  .DayPicker-Day--outside {
    cursor: default;
    color: #dce0e0;
  }

  /* Example modifiers */

  .DayPicker-Day--sunday {
    background-color: #f7f8f8;
  }

  .DayPicker-Day--sunday:not(.DayPicker-Day--today) {
    color: #dce0e0;
  }

  .DayPicker-Day--selected:not(.DayPicker-Day--disabled):not(.DayPicker-Day--outside) {
    color: #fff;
    background-color: #4a90e2;
  }

  /* DayPickerInput */

  .DayPickerInput {
    display: inline-block;
  }

  .DayPickerInput-OverlayWrapper {
    position: relative;
  }

  .DayPickerInput-Overlay {
    left: 0;
    position: absolute;
    background: white;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.15);
  }
`;

const StyledDayPicker = styled.div`
  ${addReactDayPickerStyles}

  position: absolute;
  height: 346px;
  width: 352px;
  z-index: ${({ theme }) => theme.zIndex.popover};

  .DayPicker {
    z-index: 1000;
    top: calc(100% + 1px);
    left: 0;
    background-color: var(--colorsUtilityYang100);
    box-shadow: var(--boxShadow100);
    display: block;
    overflow: hidden;
    padding: var(--spacing300);
    text-align: center;
    user-select: none;
    border-radius: var(--borderRadius050);
  }

  .DayPicker * {
    box-sizing: border-box;
  }

  .DayPicker:focus {
    outline: none;
  }

  .DayPicker abbr[title] {
    border: none;
    cursor: initial;
  }

  .DayPicker-wrapper {
    padding: 0;
    &:focus {
      outline: var(--borderWidth300) solid var(--colorsSemanticFocus500);
      border-radius: var(--borderRadius050);
    }
  }

  .DayPicker-Month {
    margin: 0 0 2px;
  }

  .DayPicker-Body,
  .DayPicker-Week {
    width: 100%;
  }

  .DayPicker-Caption {
    color: var(--colorsActionMajorYin090);
    line-height: var(--sizing500);
    height: var(--sizing500);
    //font: var(--typographyDatePickerCalendarMonthM); font assets to be updated part of FE-4975
    font-size: 16px;
    font-weight: 800;

    > div {
      margin: 0 auto;
      width: 80%;
    }
  }

  .DayPicker-Day {
    min-width: var(--sizing500);
    height: var(--sizing450);
    padding: 0;
    background-color: var(--colorsUtilityYang100);
    cursor: pointer;
    border: none;
    //font-family: var(--fontFamiliesDefault); font assets to be updated part of FE-4975
    //font: var(--typographyDatePickerCalendarDateM); font assets to be updated part of FE-4975
    font-weight: var(--fontWeights700);
    font-size: var(--fontSizes100);
    line-height: var(--lineHeights500);
    border-radius: var(--borderRadius400);

    &:hover {
      background-color: var(--colorsActionMinor050);
      color: var(--colorsActionMajorYin090);
    }

    &:focus {
      outline: var(--borderWidth300) solid var(--colorsSemanticFocus500);
    }

    + * {
      border-left: 1px;
    }

    abbr {
      text-decoration: none;
    }
  }

  .DayPicker-Day--today,
  .DayPicker-Day--today.DayPicker-Day--outside {
    color: var(--colorsActionMajorYin090);
    background-color: var(--colorsActionMinor200);
  }

  .DayPicker-Day--outside {
    color: var(--colorsActionMajorYin055);
    background-color: var(--colorsUtilityYang100);
  }

  .DayPicker-Day--disabled,
  .DayPicker-Day--disabled:hover {
    color: var(--colorsActionMajorYin030);
    background-color: var(--colorsUtilityYang100);
    cursor: default;

    &.DayPicker-Day--today {
      background-color: var(--colorsActionMinor200);
    }
  }

  .DayPicker-Day--selected:not(.DayPicker-Day--disabled):not(.DayPicker-Day--outside) {
    background-color: var(--colorsActionMajor500);
    color: var(--colorsUtilityYang100);
    border-radius: var(--borderRadius400);
  }

  .DayPicker-Day--selected.DayPicker-Day--disabled:not(.DayPicker-Day--outside) {
    background-color: var(--colorsActionMajor500);
    color: var(--colorsUtilityYang100);
  }
`;

StyledDayPicker.defaultProps = {
  theme: baseTheme,
};

export default StyledDayPicker;
